steps.verify_script:
$service = Get-Service -Name 'CloudManagedDesktopExtension' -ErrorAction SilentlyContinue
if (!$service) { exit 1 }
if ($service.Status -ne 'Running') { exit 1 }
$wmi = Get-WmiObject -Class Win32_Service -Filter "Name='CloudManagedDesktopExtension'"
if (!$wmi) { exit 1 }
if ($wmi.StartMode -ne 'Delayed Auto') { exit 1 }
if ($wmi.StartName -ne 'LocalSystem') { exit 1 }
exit 0
